import React, { useEffect, useState } from 'react';
import './Grid.css';

const GRID_ROWS = 20;
const GRID_COLS = 15;
const INITIAL_INTERVAL = 200; // Adjust the interval for better effect

const getRandomInt = (max) => Math.floor(Math.random() * max);

const Grid = () => {
  const [grid, setGrid] = useState(Array(GRID_ROWS).fill().map(() => Array(GRID_COLS).fill(false)));
  const [colors, setColors] = useState(Array(GRID_ROWS).fill().map(() => Array(GRID_COLS).fill(null)));
  
  useEffect(() => {
    const intervalId = setInterval(() => {
      setGrid((prevGrid) => {
        const newGrid = Array(GRID_ROWS).fill().map(() => Array(GRID_COLS).fill(false));
        const newColors = Array(GRID_ROWS).fill().map(() => Array(GRID_COLS).fill(null));

        for (let col = 0; col < GRID_COLS; col++) {
          let emptyRow = GRID_ROWS - 1;
          while (emptyRow >= 0 && prevGrid[emptyRow][col]) {
            emptyRow--;
          }
          if (emptyRow >= 0) {
            newGrid[emptyRow][col] = true;
            newColors[emptyRow][col] = `hsl(${getRandomInt(360)}, 100%, 50%)`;
          }
        }

        return newGrid;
      });

      setColors((prevColors) => {
        const newColors = prevColors.map((row) => row.slice());

        for (let col = 0; col < GRID_COLS; col++) {
          let emptyRow = GRID_ROWS - 1;
          while (emptyRow >= 0 && newColors[emptyRow][col]) {
            emptyRow--;
          }
          if (emptyRow >= 0) {
            newColors[emptyRow][col] = `hsl(${getRandomInt(360)}, 100%, 50%)`;
          }
        }

        return newColors;
      });
    }, INITIAL_INTERVAL);
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div className="row" key={rowIndex}>
          {row.map((cell, colIndex) => (
            <div
              className="cell"
              key={colIndex}
              style={{ backgroundColor: colors[rowIndex][colIndex] }}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default Grid;
