import React, { useEffect, useState } from 'react';
import './Grid.css';

const GRID_ROWS = 20;
const GRID_COLS = 15;
const FALL_INTERVAL = 200; // Adjust the interval for better effect
const MAX_INTENSITY = 5; // Maximum intensity for the drop

const getRandomInt = (max) => Math.floor(Math.random() * max);

const Grid = () => {
  const [grid, setGrid] = useState(Array(GRID_ROWS).fill().map(() => Array(GRID_COLS).fill(false)));
  const [colors, setColors] = useState(Array(GRID_ROWS).fill().map(() => Array(GRID_COLS).fill(null)));
  const [activeDrops, setActiveDrops] = useState([]); // Array to keep track of active raindrops

  useEffect(() => {
    const intervalId = setInterval(() => {
      setGrid((prevGrid) => {
        const newGrid = prevGrid.map((row) => row.slice());
        const newColors = colors.map((row) => row.slice());

        // Update active drops
        const newActiveDrops = activeDrops.map(drop => {
          const { row, col, intensity } = drop;
          if (row < GRID_ROWS - 1) {
            newGrid[row][col] = false; // Clear the current cell
            newGrid[row + 1][col] = true; // Move the drop one row down
            newColors[row + 1][col] = `rgba(255, 0, 255, ${intensity / MAX_INTENSITY})`; // Set the color with varying intensity
            return { row: row + 1, col, intensity: Math.max(intensity - 1, 1) }; // Update the drop position and decrease intensity
          }
          return drop; // Keep the drop in the same position if it reached the bottom
        }).filter(drop => drop.row < GRID_ROWS); // Remove drops that have reached the bottom

        // Add new raindrop
        if (Math.random() < 0.1) { // Adjust the probability to control the frequency of new drops
          const col = getRandomInt(GRID_COLS);
          newActiveDrops.push({ row: 0, col, intensity: MAX_INTENSITY });
          newGrid[0][col] = true;
          newColors[0][col] = `rgba(255, 0, 255, 1)`;
        }

        setColors(newColors);
        setActiveDrops(newActiveDrops);
        return newGrid;
      });
    }, FALL_INTERVAL);

    return () => clearInterval(intervalId);
  }, [activeDrops, colors]);

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div className="row" key={rowIndex}>
          {row.map((cell, colIndex) => (
            <div
              className="cell"
              key={colIndex}
              style={{ backgroundColor: colors[rowIndex][colIndex] }}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default Grid;
