import React, { useEffect, useState } from 'react';
import './App.css';
import './Grid.css';

const numRows = 20;
const numCols = 20;
const maxActiveColumns = 6; // Maximum number of active rainfall columns

const App = () => {
  const [grid, setGrid] = useState(createGrid());
  const [colorIndex, setColorIndex] = useState(0);
  const [currentColors, setCurrentColors] = useState([]);

  const colors = [
    ['#ff00ff', '#e600e6', '#cc00cc', '#b300b3', '#990099'], // Five intensities for each color
    ['#00ffff', '#00e6e6', '#00cccc', '#00b3b3', '#009999'],
    ['#ffff00', '#e6e600', '#cccc00', '#b3b300', '#999900']
  ];

  useEffect(() => {
    // Initialize the currentColors with the first color set
    setCurrentColors(colors[colorIndex]);

    const colorChangeInterval = setInterval(() => {
      setColorIndex(prevIndex => (prevIndex + 1) % colors.length);
    }, 5000); // Change color every 5 seconds

    // Initialize multiple rainfalls with random delays
    for (let i = 0; i < maxActiveColumns; i++) {
      startRainfallWithRandomDelay();
    }

    return () => {
      clearInterval(colorChangeInterval);
    };
  }, []);

  useEffect(() => {
    setCurrentColors(colors[colorIndex]);
  }, [colorIndex]);

  const startRainfallWithRandomDelay = () => {
    const randomDelay = Math.random() * 2000; // Random delay between 0 and 2 seconds
    setTimeout(() => {
      createRainfall();
      startRainfallWithRandomDelay(); // Recursively start another rainfall after a random delay
    }, randomDelay);
  };

  const createRainfall = () => {
    const randomColumn = Math.floor(Math.random() * numCols);

    for (let i = 0; i < numRows + 5; i++) {
      setTimeout(() => {
        setGrid(prevGrid => {
          const newGrid = prevGrid.map((row, rowIndex) =>
            row.map((cell, colIndex) => {
              if (colIndex === randomColumn) {
                if (rowIndex >= i && rowIndex < i + 5) {
                  return currentColors[rowIndex - i];
                } else if (rowIndex === i - 5) {
                  return false;
                }
              }
              return cell;
            })
          );
          return newGrid;
        });
      }, i * 100);
    }
  };

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cell, colIndex) => (
            <div
              key={colIndex}
              className="cell"
              style={{ backgroundColor: cell ? cell : '' }}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

const createGrid = () => {
  const grid = [];
  for (let i = 0; i < numRows; i++) {
    const row = [];
    for (let j = 0; j < numCols; j++) {
      row.push(false);
    }
    grid.push(row);
  }
  return grid;
};

export default App;
