import React, { useEffect, useState } from 'react';
import './Grid.css';

const numRows = 20;
const numCols = 20;

const App = () => {
  const [grid, setGrid] = useState(createGrid());
  const [colorIndex, setColorIndex] = useState(0);

  const colors = [
    ['#ff00ff', '#cc00cc', '#990099', '#660066'],
    ['#00ffff', '#00cccc', '#009999', '#006666'],
    ['#ffff00', '#cccc00', '#999900', '#666600']
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      createRainfall();
    }, 2000);

    const colorChangeInterval = setInterval(() => {
      setColorIndex(prevIndex => (prevIndex + 1) % colors.length);
    }, 5000);

    return () => {
      clearInterval(interval);
      clearInterval(colorChangeInterval);
    };
  }, []);

  const createRainfall = () => {
    const columns = getRandomColumns(6);

    columns.forEach(randomColumn => {
      for (let i = 0; i < numRows; i++) {
        setTimeout(() => {
          setGrid(prevGrid => {
            const newGrid = prevGrid.map((row, rowIndex) =>
              row.map((cell, colIndex) => {
                if (colIndex === randomColumn && rowIndex >= i && rowIndex < i + 4) {
                  return colors[colorIndex][rowIndex - i];
                }
                return cell;
              })
            );
            return newGrid;
          });
        }, i * 100);
      }

      setTimeout(() => {
        setGrid(prevGrid => {
          const newGrid = prevGrid.map((row, rowIndex) =>
            row.map((cell, colIndex) => {
              if (colIndex === randomColumn) {
                return false;
              }
              return cell;
            })
          );
          return newGrid;
        });
      }, numRows * 100);
    });
  };

  const getRandomColumns = (num) => {
    const columns = [];
    while (columns.length < num) {
      const randomColumn = Math.floor(Math.random() * numCols);
      if (!columns.includes(randomColumn)) {
        columns.push(randomColumn);
      }
    }
    return columns;
  };

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cell, colIndex) => (
            <div
              key={colIndex}
              className="cell"
              style={{ backgroundColor: cell ? cell : '' }}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

const createGrid = () => {
  const grid = [];
  for (let i = 0; i < numRows; i++) {
    const row = [];
    for (let j = 0; j < numCols; j++) {
      row.push(false);
    }
    grid.push(row);
  }
  return grid;
};

export default App;
