import React, { useEffect, useState } from 'react';
import './App.css';

const numRows = 20;
const numCols = 20;

const App = () => {
  const [grid, setGrid] = useState(createGrid());

  useEffect(() => {
    const interval = setInterval(() => {
      createRainfall();
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  const createRainfall = () => {
    const randomColumn = Math.floor(Math.random() * numCols);
    const colors = ['#ff00ff', '#cc00cc', '#990099', '#660066']; // Decreasing intensity colors

    for (let i = 0; i < 4; i++) {
      setTimeout(() => {
        setGrid(prevGrid => {
          const newGrid = prevGrid.map((row, rowIndex) =>
            row.map((cell, colIndex) => {
              if (colIndex === randomColumn && rowIndex === i) {
                return colors[i];
              }
              return cell;
            })
          );
          return newGrid;
        });
      }, i * 100);
    }

    // Reset the column after the animation
    setTimeout(() => {
      setGrid(prevGrid => {
        const newGrid = prevGrid.map((row, rowIndex) =>
          row.map((cell, colIndex) => {
            if (colIndex === randomColumn && rowIndex < 4) {
              return false;
            }
            return cell;
          })
        );
        return newGrid;
      });
    }, 600); // Wait for the animation to complete before resetting
  };

  return (
    <div className="grid">
      {grid.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cell, colIndex) => (
            <div
              key={colIndex}
              className="cell"
              style={{ backgroundColor: cell ? cell : '' }}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

const createGrid = () => {
  const grid = [];
  for (let i = 0; i < numRows; i++) {
    const row = [];
    for (let j = 0; j < numCols; j++) {
      row.push(false);
    }
    grid.push(row);
  }
  return grid;
};

export default App;
